import { createServer } from "http";

// server/storage.ts
var MemStorage = class {
  users;
  questionnaireResponses;
  currentUserId;
  currentQuestionnaireId;
  constructor() {
    this.users = /* @__PURE__ */ new Map();
    this.questionnaireResponses = /* @__PURE__ */ new Map();
    this.currentUserId = 1;
    this.currentQuestionnaireId = 1;
  }
  async getUser(id) {
    return this.users.get(id);
  }
  async getUserByUsername(username) {
    return Array.from(this.users.values()).find(
      (user) => user.username === username
    );
  }
  async createUser(insertUser) {
    const id = this.currentUserId++;
    const user = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }
  async createQuestionnaireResponse(insertResponse) {
    const id = this.currentQuestionnaireId++;
    const response = { ...insertResponse, id };
    this.questionnaireResponses.set(id, response);
    return response;
  }
  async getQuestionnaireStats() {
    const responses = Array.from(this.questionnaireResponses.values());
    const total = responses.length;
    const science = responses.filter((r) => r.recommendation.toLowerCase().includes("science")).length;
    const english = responses.filter((r) => r.recommendation.toLowerCase().includes("english")).length;
    return { total, science, english };
  }
};
var storage = new MemStorage();

// shared/schema.ts
import { pgTable, text, serial } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
var users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull()
});
var questionnaireResponses = pgTable("questionnaire_responses", {
  id: serial("id").primaryKey(),
  q1: text("q1").notNull(),
  q2: text("q2").notNull(),
  q3: text("q3").notNull(),
  q4: text("q4").notNull(),
  recommendation: text("recommendation").notNull(),
  timestamp: text("timestamp").notNull()
});
var insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true
});
var insertQuestionnaireResponseSchema = createInsertSchema(questionnaireResponses).pick({
  q1: true,
  q2: true,
  q3: true,
  q4: true,
  recommendation: true,
  timestamp: true
});

// server/routes.ts
async function registerRoutes(app2) {
  app2.post("/api/questionnaire", async (req, res) => {
    try {
      const data = insertQuestionnaireResponseSchema.parse(req.body);
      const response = await storage.createQuestionnaireResponse(data);
      res.json(response);
    } catch (error) {
      res.status(400).json({ error: "Invalid questionnaire data" });
    }
  });
  app2.get("/api/questionnaire/stats", async (req, res) => {
    try {
      const stats = await storage.getQuestionnaireStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ error: "Failed to get statistics" });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

